import {
    Box,
    Flex,
    Grid,
    Text,
    Link,
    Image,
    SimpleGrid
} from "@chakra-ui/core";

import { useWeb3React } from '@web3-react/core'
import React, { useEffect, useState } from "react";

export default function VaultList(props: any) {

    const { account, chainId } = useWeb3React()
    const [vaults, setVaults] = React.useState([])
    const validImage = function(data){
      if (data.includes('http')) {
        return true
      } else {
        return false
      }
    }
    const getVaults = async () => {
      // const responce = await fetch('https://api.emblemvault.io/vaults/0x5a63264914a1eCB626e32e8AD683704bA7b0621f', {
      const responce = await fetch('https://api.emblemvault.io/vaults/' + account, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'service': 'evmetadata',
          'chainId': chainId.toString()
        },
      })
      const jsonData = await responce.json()
      setVaults(jsonData)
    }

    useEffect(() => {
        getVaults()
      }, [])

    return (
      <Flex width="full" align="center" justifyContent="center" justifyItems="center">
        {vaults.length ? vaults.map((vault) => {
            var url = './vault/?id=' + vault.tokenId
            const flexSettings = {
              flex: "1",
              minW: "300px",
              maxW: "250px",
              // textAlign: "center",
              color: "white",
              mx: "6",
              mb: "6"
            };
            return (
              <Flex w="100%" justify="center" flexWrap="wrap">
                <Box borderWidth="1px" rounded="lg" overflow="hidden" {...flexSettings} > 
                  <Text  w="100%" p={0}>Nothing in here! Fill 'er up!</Text>
                  <Image src={validImage(vault.image) ? vault.image : "https://circuitsofvalue.com/public/coval-logo.png" } size="250px"/>
                </Box>
                <Box {...flexSettings} bg={"blue.500"}>I'm a box 2</Box>
                <Box {...flexSettings} bg={"green.500"}>I'm a box 3</Box>
                <Box {...flexSettings} bg={"green.500"}>I'm a box 3</Box>
                <Box {...flexSettings} bg={"green.500"}>I'm a box 3</Box>
                <Box {...flexSettings} bg={"green.500"}>I'm a box 3</Box>
              </Flex>
              // <Grid key={vault.tokenId} templateColumns="repeat(5, 1fr)" gap={6}>
              //     <Link href={url}>
              //       <Box maxW="sm" borderWidth="1px" rounded="lg" overflow="hidden" >
              //         <Box
              //           mt="1"
              //           fontWeight="semibold"
              //           as="h4"
              //           lineHeight="tight"
              //           isTruncated
              //           p={1}
              //           textAlign="center"
              //         >
              //           {vault.name}

              //           <Image src={validImage(vault.image)
              //             ? vault.image
              //             : "https://circuitsofvalue.com/public/coval-logo.png"
              //           }
              //             size="250px"
              //           />
              //         </Box>

              //         <Box p="4">
              //           <Box d="flex" alignItems="baseline">
              //             <Box
              //               color="gray.500"
              //               fontWeight="semibold"
              //               letterSpacing="wide"
              //               fontSize="xs"
              //               textTransform="uppercase"
              //               ml="1"
              //             >
              //               {vault.values.length
              //                 ? vault.values.map((coin) => {
              //                   return (
              //                     <Text key={coin.name}>
              //                       {coin.balance} {coin.name}
              //                     </Text>
              //                   )
              //                 })
              //                 : <Text  w="100%" p={0}>Nothing in here! Fill 'er up!</Text>
              //               }
              //             </Box>
              //           </Box>
              //         </Box>
              //       </Box>
              //     </Link>
              //   </Grid>

            )
          })
          :
          <Text>
            YOU DON'T SEEM TO HAVE ANY VAULTS. {" "}
            <Link color="#638cd8" href="../create">
              CREATE ONE HERE!
            </Link>
          </Text>
        }
      </Flex>
    )

}
