import React, { Component } from 'react'
import {
  Box,
  Image,
  Text,
  Link,
  useColorMode,
  Button,
  Flex,
  PseudoBox
} from '@chakra-ui/core'

import {HStack, VStack, Circle } from '@chakra-ui/react'
import { Contract } from '@ethersproject/contracts'

const getContract = (address, abi, library, account): any =>{
  return new Contract(address, abi, library.getSigner(account).connectUnchecked())
}

async function checkApproval(handler: { address: string; abi: any }, spending: { address: string; abi: any }, library: any, account: any, amount: number) {
  if (amount > 0) {
    let spendingContract = getContract(spending.address, spending.abi, library, account)
    let foo = await spendingContract.allowance(account, handler.address)
    alert(foo)
  }
  return false
}

type AttributesProps = {
  handler: {address: string, abi: any},
  spending: {address: string, abi: any},
  amount: number,
  label: string,
  library: any,
  account: any,
}

export class Approval extends Component<AttributesProps> {
  render() {
    let handler = this.props.handler
    let spending = this.props.spending
    let amount = this.props.amount
    let label = this.props.label
    let library = this.props.library
    let account = this.props.account
    let approved = checkApproval(handler, spending, library, account, amount)
    return approved? (
      <></>
    ) : (
      <Flex w="100%" flexWrap="wrap" mt={3} >
        <Button backgroundColor={"#02b402"} color={"black !important"} fontWeight={"bold !important"} width="100%">
          {label}
        </Button>
      </Flex>
    )
  }
}

export default Approval
