import React, { Component } from 'react'
import {
  Box,
  Image,
  Text,
  Link,
  useColorMode,
  Button,
  Flex,
  PseudoBox
} from '@chakra-ui/core'

import {HStack, VStack, Circle } from '@chakra-ui/react'

type ListingsProps = {
  listings: any[],
  colorMode: string,
}

export class Listings extends Component<ListingsProps> {
  render() {
    let colorMode = this.props.colorMode
    let listings = this.props.listings
    return (
      <Flex w="100%" flexWrap="wrap" mt={3} >
        {
          listings.map((listing, index)=>{
            return (

              <Box mt="2px" p={1} ml="3px" minWidth="45%" /*maxWidth="50%"*/ width="98%"key={0}  >
                <Text position={'relative'} left={'20px'} float={'right'} ml={2} mr={Number.isInteger(index/2) ? 0: "4px"} fontSize=".9em" textAlign={Number.isInteger(index/2) ? "left": "right"} fontWeight="bold" overflow="hidden" isTruncated={true} >{listing.price.amount.decimal} {listing.price.currency.name} (${listing.price.amount.usd})</Text>
                {/* <Text ml={2} mr={Number.isInteger(index/2) ? 0: "4px"} fontSize=".9em" textAlign={Number.isInteger(index/2) ? "left": "right"} fontWeight="bold" overflow="hidden" isTruncated={true} >{listing.source.icon}</Text> */}
                <Image position={'relative'} float={'left'} width={'20px'} src={listing.source.icon}></Image>
                <Link href={''} left={'5px'} position={'relative'} fontSize=".9em"> {listing.source.name}</Link>

              </Box>
            )
          })
        }
      </Flex>
    )
  }
}

export default Listings
