  render() {
    let items = this.props.items
    return (
      <div>        
        <h2>Fade Effect</h2>
        <Slide {...this.props.properties}>
          {items.map((item, index)=>{
            console.log(item.image)
            return (
              <div key={index} className="each-fade">
                <div>
                  <img src={item.image} />
                </div>
                <p>First Slide</p>
              </div>
            )
          })}
        </Slide>

        {/* <div className="slide-container">
          <Slide {...this.props.properties}>
