    var raw = JSON.stringify({ signature: signature })
    const response = await fetch(EMBLEM_API + '/embed/' + tokenId, {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow',
    })
    const jsonData = await response.json()
    return cb(jsonData)
  }

