import React, { Component } from 'react'
import { Box, Flex, Text, Link, Image, Stack, Button, BoxProps, Input, Select, Tabs, Tab, TabList, TabPanels, TabPanel } from '@chakra-ui/core'
import { contractAddresses } from '../../constants'
import { Contract } from '@ethersproject/contracts'
type Props = {
  address: string,
  account: string,
  library: any
}

export class ERC1155Contract extends Component<Props> {
  async render() {
    let address = this.props.address
    let account = this.props.account
    let library = this.props.library
    let abi = contractAddresses.erc1155Abi
    let tokenContract = new Contract(address, abi, library.getSigner(account).connectUnchecked())
    let tokenName = await tokenContract.name()
    return (
      <>
        <Text>{tokenName}</Text>
      </>
    )
  }
}

export default ERC1155Contract
