// import { Box, Flex, Text, Link, Image, Stack, Spinner, useColorMode } from '@chakra-ui/core'
import { Box, Flex, Text, Link, Image, Stack, Button, FormControl, FormLabel, Checkbox, CheckboxGroup, Radio, RadioGroup, Input, FormHelperText, Select, useColorMode } from '@chakra-ui/core'
import {HStack, VStack } from '@chakra-ui/react'
import Loader from 'react-loader'
import Refreshing from './Refreshing'
import { useRouter } from 'next/router'
import { useWeb3React } from '@web3-react/core'
import { useEffect, useState } from 'react'
import { EMBLEM_API } from '../constants'
import InfiniteScroll from 'react-infinite-scroll-component';
import Embed from './Embed'

export default function Find() {
  const { query } = useRouter()
  const [pagePosition, setPagePosition] = useState(Number(query.start) || 0)
  const { account, chainId } = useWeb3React()
  const [vaults, setVaults] = useState([])
  const [state, setState] = useState({ loaded: true })
  const [loadingApi, setLoadingApi] = useState(false)
  const [address, setAddress] = useState(query.address)
  const [experimental, setExperimental] = useState(query.experimental)
  const { colorMode } = useColorMode()
  const [shouldFetchData, setShouldFetchData] = useState(false)
  const [hasMore, setHasMore] = useState(true)
  const [offset, setOffset] = useState(0)
  const [q, setQ] = useState(query.q)
  const [temp_q, setTempQ] = useState(q || '')

  const [assetName, setAssetName] = useState(query.assetName)
  const [tempAssetName, setTempAssetName] = useState(assetName || '')

  const [coin, setCoin] = useState(query.coin)
  const [tempCoin, setTempCoin] = useState(coin || '')

  const [project, setProject] = useState(query.project)
  const [tempProject, setTempProject] = useState(project || '')

  const [projects, setProjects] = useState([])
  const [loadedProjects, setLoadedProjects] = useState(false)

  const [searchType, setSearchType] = useState(query.searchType || 'filter')
  const [tempSearchType, setTempSearchType] = useState(searchType.toString())

  const [network, setNetwork] = useState(query.network || null)

  const [balanceChecks, setBalanceChecks] = useState([])
  const [metadataChecks, setMetadataChecks] = useState([])

  const PAGE_SIZE = 20

  const getProjects = async () => { 
    try {
      const response = await fetch(EMBLEM_API + '/search/projects', {
        method: 'GET',
        headers: {
          service: 'evmetadata'
        }
      })
      let jsonData = await response.json()
      console.log('------ projects', jsonData)

      setProjects(jsonData)
    } catch (error) {}
  } 

  const getVaults = async () => {    
    console.log('getting vaults')
    try {
      const response = await fetch(EMBLEM_API + '/find/'+searchType+'?start='+offset+'&size='+PAGE_SIZE, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          service: 'evmetadata'
        },
        body: JSON.stringify({
          "metadata": {
            "name": q,
            "description": q
          }
        })
      })
      let jsonData = await response.json()
      // if (jsonData) {
      //   jsonData = jsonData.filter(item=>{return item.live && !item.claimedBy})
      // }
      console.log('------ vaults', jsonData)
      jsonData.length == 0 || jsonData.length < PAGE_SIZE ? setHasMore(false) : setHasMore(true)
      setVaults(vaults.concat(jsonData))
      setState({ loaded: true })
      setLoadingApi(false)
    } catch (error) {}
  }

  const fetchData = async() =>{
    console.log(offset, PAGE_SIZE, offset+PAGE_SIZE)
    setOffset(offset+PAGE_SIZE)
    setShouldFetchData(true)
  }  

  const [acct, setAcct] = useState('')
  useEffect(() => {
    if (account && acct != account) {
      setAcct(account)
      setState({ loaded: false })
      getVaults()
    }
  }, [account, acct])

  const [chain, setChain] = useState(chainId)
  useEffect(() => {
    if (chainId && chain != chainId) {
      setChain(chainId)
      setState({ loaded: false })
      getVaults()
    }
  }, [chainId, chain])

  useEffect(() => {
    // console.log('here', chainId, chain, account, state)
    if (!account && !state.loaded) {      
      setState({ loaded: false })
      getVaults()
    }
  }, [chainId, chain])

  useEffect(() => {    
    if (shouldFetchData) {
      console.log('here 1')
      setShouldFetchData(false)
      setLoadingApi(true)
      getVaults()
    } else {
      console.log('no fetch')
    }
  }, [shouldFetchData])

  useEffect(() => {
    if (q && vaults.length == 0 && !loadingApi) {
      console.log('here 2')
      setShouldFetchData(true)
      // getVaults()
    } else {
      console.log('no fetch', q, vaults)
    }
  }, [q, vaults])

  useEffect(() => {
    if (projects.length == 0 && !loadedProjects) {
      setLoadedProjects(true)
      getProjects()
    }
  })

  function addOrRemoveBalanceFilter(target) {
    if (balanceChecks.includes(target)) {
      setBalanceChecks(balanceChecks.filter(item => item !== target))
    } else {
      setBalanceChecks(balanceChecks.concat(target))
    }
    console.log('balance checks',balanceChecks)
  }

  function addOrRemoveMetadataFilter(target) {
    if (metadataChecks.includes(target)) {
      setMetadataChecks(metadataChecks.filter(item => item !== target))
    } else {
      setMetadataChecks(metadataChecks.concat(target))
    }
    console.log('metadata checks',metadataChecks)
  }

  return (
    <Loader loaded={state.loaded}>
      {loadingApi ? <Refreshing /> : ''}
      {/* <Flex className="wtf" width="100%" align="center" justifyContent="center" > */}
      <VStack width="100%" align="center" justifyContent="center" >
        <RadioGroup onChange={(e)=>{ setTempSearchType(e.target.value) }} defaultValue={tempSearchType} value={tempSearchType}>
          <Stack spacing={4} direction="row">                
            <Radio isChecked={tempSearchType == "filter"? true : false} value="filter">Filter (AND)</Radio>
            <Radio isChecked={tempSearchType == "or"? true : false} value="or">Search (OR)</Radio>
          </Stack>
        </RadioGroup>
        <CheckboxGroup>
          <HStack>
            <Checkbox value="name" onChange={(e)=>{addOrRemoveBalanceFilter(e.target.value)}} >Asset Name</Checkbox>
            <Checkbox value="coin" onChange={(e)=>{addOrRemoveBalanceFilter(e.target.value)}} >Blockchain</Checkbox>
            <Checkbox value="project" onChange={(e)=>{addOrRemoveBalanceFilter(e.target.value)}} >Project</Checkbox>
            <Checkbox value="vaultName" onChange={(e)=>{addOrRemoveMetadataFilter(e.target.value)}} >Vault Name</Checkbox>
            <Checkbox value="vaultDescription" onChange={(e)=>{addOrRemoveMetadataFilter(e.target.value)}} >Vault Description</Checkbox>
          </HStack>
        </CheckboxGroup>
        {/* {balanceChecks.length > 0 ? (
          <Text>Find within vault balances </Text>
        ) : null} */}
        {balanceChecks.map((check, index)=>{
          return (
            <VStack>
              <Text>{index == 0 ? null : (searchType == "or"? "or": "and")} {check == "coin" ? "from Blockchain": check=="name" ? "within balances for the Asset Name of": "of the Project"}</Text>
              {check == "name" ? (
                <Input value={tempAssetName} onChange={(e) => setTempAssetName(e.target.value)} ></Input>
              ) : check == "coin" ? (
                <Select value={tempCoin} onChange={(e)=>{setTempCoin(e.target.value)}}>
                  <option value="POLYGON">Polygon (Matic)</option>
                  <option value="XCP">Counterparty</option>
                  <option value="BTC">Bitcoin</option>
                  <option value="BCH">Bitcoin Cash</option>
                  <option value="DGB">Digibyte</option>
                  <option value="DOGE">Dogecoin</option>
                  <option value="ETH">Ethereum</option>
                  <option value="FTM">Fantom</option>
                  <option value="LTC">Litecoin</option>
                  <option value="NMC">Namecoin</option>
                  <option value="OMNI">Omni</option>
                  <option value="xDai">xDai</option>
                  <option value="OMNI">Omni</option>
                  <option value="XDP">Doge Party</option>
                  <option value="XMP">Mona Party</option>
                  <option value="XTZ">Tezos</option>
                </Select>
              ) : check == "project" ? (
                <Select value={tempProject} onChange={(e)=>{setTempProject(e.target.value)}}>
                  {projects.map((project, index)=>{
                    return (<option value={project}>{project}</option>)
                  })}
                </Select>
              ) : null}
            </VStack>
          )
        })}
        {/* {metadataChecks.length > 0 ? (
          <Text>Find within vault </Text>
        ) : null} */}
        {metadataChecks.map((check, index)=>{
          return (
            <VStack>
              <Text>{index == 0 ? null : (searchType == "or"? "or": "and")} {check == "vaultName" ? "within vault name": check=="vaultDescription" ? "within vault description": null}</Text>
              <Input></Input>
            </VStack>
          )})}
      </VStack>
        {/* <FormControl isRequired> */}
          {/* <Flex align="center" justifyContent="center" flexWrap="wrap"> */}
            {/* <Input
              type="text"
              id="search-query"
              width='45%'
              aria-describedby="search-query-helper-text"
              placeholder="0xdeadbeef"
              maxLength={42}
              value={temp_q}
              onChange={(e) =>
                setTempQ(e.target.value)
              }
            /> */}
            <Button
              m={2}
              onClick={() => {
                setOffset(0)
                setState({ loaded: false })
                setVaults([])                
                setQ(temp_q)
                let loc = location.href.split('?')[0]+"?q="+temp_q +  "&network=" + network
                window.history.pushState(temp_q, 'Title', loc);
              }}
            >Search
            </Button>
            {/* <Stack align="center" w="100%" mt={3}> */}
              {/* <Select w="45%" placeholder="All Networks" value={network}
                onChange={(e)=>{
                  setNetwork(e.target.value)
                  console.log(e.target.value)
                }}
              >
                <option value="matic">Matic</option>
                <option value="xdai">xDai</option>
                <option value="bsc">Binance Smart Chain</option>
                <option value="mainnet">Ethereum Mainnet</option>
                <option value="rinkeby">Ethereum Rinkeby</option>
              </Select> */}
              {/* <RadioGroup onChange={(e)=>{ setSearchType(e.target.value) }} defaultValue={searchType} value={searchType}>
                <Stack spacing={4} direction="row">                
                  <Radio isChecked={searchType == "filter"? true : false} value="filter">Filter (AND)</Radio>
                  <Radio isChecked={searchType == "or"? true : false} value="or">Search (OR)</Radio>
                </Stack>
              </RadioGroup>

              <FormHelperText id="search-query-helper-text">
                <HStack>
                  {balanceChecks.length > 0 ? (
                    searchType == "filter" ? (
                      <Text>And</Text>
                    ) : (
                      balanceChecks.map((check, index)=>{
                        return <Text>{index == 0 ? ('Search Vault Balances for') : ('for the ')} 
                        {check == "name" ? (
                          <HStack mt={10}>
                            <Text>Asset name of </Text>
                            <Input></Input>
                          </HStack>
                        ): 
                        check == "coin" ? (
                          <HStack mt={10}>
                            <Text>Blockchain </Text>
                            <Input></Input>
                          </HStack>
                        ): check == "project" ? ("Project") : null} {((balanceChecks.length -1) == index) ? null : ('or')}</Text>
                      })
                    )
                  ) : (
                    <Text>Balance Filters</Text>
                  ) }
                </HStack>
              </FormHelperText>

              <CheckboxGroup>
                <HStack>
                  <Checkbox value="name" onChange={(e)=>{addOrRemoveBalanceFilter(e.target.value)}} >Asset Name</Checkbox>
                  <Checkbox value="coin" onChange={(e)=>{addOrRemoveBalanceFilter(e.target.value)}} >Blockchain</Checkbox>
                  <Checkbox value="project" onChange={(e)=>{addOrRemoveBalanceFilter(e.target.value)}} >Project</Checkbox>
                </HStack>
              </CheckboxGroup>

            </Stack> */}
          {/* </Flex> */}

          {/* <FormHelperText id="search-query-helper-text">
            Search for Emblem Vaults by: Name, Description, Type, Contents
          </FormHelperText> */}
        {/* </FormControl> */}
      {/* </Flex> */}
      {q? (
        <InfiniteScroll                
          className="infinite-scroll"
          scrollableTarget="shannon-container"
          // height={200}
          dataLength={vaults.length} //This is important field to render the next data
          next={fetchData}
          hasMore={hasMore}
          loader={<Refreshing />}
          endMessage={
            <p style={{ textAlign: 'center' }}>
              <b>Yay! You have seen it all</b> 
            </p>
          }
        >
          <Flex w="100%" justify="center" flexWrap="wrap" mt={10}>        
            {vaults.length ? (
              vaults.map((vault, index) => {
                let pieces = location.pathname.split('/')
                pieces.pop()
                let url = location.origin + pieces.join('/') + '/nft?id=' + vault.tokenId
                const flexSettings = {
                  flex: '1',
                  minW: '200px',
                  maxW: '200px',
                  borderWidth: '1px',
                  // color: 'white',
                  mx: '6',
                  mb: '6',
                  rounded: 'lg',
                  overflow: 'hidden',
                  borderColor: vault.status == 'claimed' ? 'green !important' : '',
                  cursor: 'pointer',
                }
                const redirect = function () {
                  setLoadingApi(true)
                  location.href = url
                }
                return (
                  <Box className="NFT newest" key={index} {...flexSettings} onClick={redirect}>
                    <Text fontWeight="semibold" textAlign="center" mt={2} pl={2} isTruncated={true}>
                      {vault.name}
                      {!vault.private && vault.totalValue > 0 ? ': ~$' + vault.totalValue : null}
                    </Text>
                    <Stack align="center">
                      <Embed className="d-block w-100 NFT-newest-image" url={vault.image}/>
                    </Stack>
                    <Box d="flex" alignItems="baseline">

                    </Box>

                    <Stack align="center" mt={3}>
                      {vault.status == 'claimed' ? <Text color="green.500">CLAIMED</Text> : null}
                      <Text fontSize="xs">Id: {vault.tokenId}</Text>
                    </Stack>
                  </Box>
                )
              })

            ) : (
              <Text>
                YOU DON'T SEEM TO HAVE ANY VAULTS.{' '}
                <Link color="#638cd8" href="../create">
                  CREATE ONE HERE!
                </Link>
              </Text>
            )}

          </Flex>
        </InfiniteScroll>
      ) : null}
    </Loader>
  )
}
