import React, { useEffect, useState } from 'react'
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  Text,
  Stack,
  Button,
  useColorMode,
} from '@chakra-ui/core'
import copy from 'copy-to-clipboard'
import QRCode from 'qrcode.react'
import { COLOR } from '../constants'
import { useWeb3React } from '@web3-react/core'

export const EMBLEM_API = 'https://api.emblemvault.io'

export default function OfferModal({
  isOpen,
  onClose,
//   addrCoin,
//   addrAddr,
}: {
  isOpen: boolean
  onClose: () => void
//   addrCoin: string
//   addrAddr: string
}): JSX.Element {
//   const address = addrAddr
//   const coin = addrCoin

  const { colorMode } = useColorMode()
  const { account, chainId, library } = useWeb3React()
  const [copied, setCopied] = useState(false)
  useEffect(() => {
    if (copied) {
      const timeout = setTimeout(() => {
        setCopied(false)
      }, 750)
      return (): void => {
        clearTimeout(timeout)
      }
    }
  }, [copied])

  useEffect(() => {

  })

  const getEthBalances = async (address, cb) => {
    const responce = await fetch(EMBLEM_API + '/eth/balance/' + address, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        service: 'evmetadata',
      },
    })
    const jsonData = await responce.json()
    // console.log(Number(vaultTotalValue), Number(jsonData.totalValue))
    setVaultTotalValue(Number(vaultTotalValue) + Number(jsonData.totalValue))
    // console.log('get eth balances', jsonData.values)
    return cb(jsonData.values)
  }

  function copyWithFlag(content: string): void {
    copy(content)
    setCopied(true)
  }

  return (
    <Modal isOpen={isOpen} onClose={onClose} isCentered>
      <ModalOverlay />
      <ModalContent color={COLOR[colorMode]}>
        <ModalHeader>
          <Text>Offer</Text>
        </ModalHeader>
        <ModalCloseButton />
        <ModalBody>
          <Stack direction="column">
            <Stack direction="row" align="center" justify="center">
              {/* <QRCode value={coin=="BTC" ? "bitcoin:"+address: address} onClick={() => copyWithFlag(address)} /> */}
            </Stack>

            <Stack direction="row" justify="center" mt="6">
              <Text>{account} address (click to copy)</Text>
            </Stack>
            <Stack direction="row" justify="center">
              <Button onClick={() => copyWithFlag('address')}>
                {copied ? <Text>Copied!</Text> : <Text isTruncated>{'address'}</Text>}
              </Button>
            </Stack>
          </Stack>
        </ModalBody>
        <ModalFooter></ModalFooter>
      </ModalContent>
    </Modal>
  )
}
