import { Box, Flex, Text, Link, Image, Stack, Spinner } from '@chakra-ui/core'
import Loader from 'react-loader'
import Refreshing from './Refreshing'
import { useRouter } from 'next/router'
import { useWeb3React } from '@web3-react/core'
import { useEffect, useState } from 'react'
import { validImage } from '../utils'
import { useContract } from '../hooks'
import { EMBLEM_API, contractAddresses } from '../constants'

export default function SwapChain() {
  const { query } = useRouter()
  const { account, chainId } = useWeb3React()
  // const [vaults, setVaults] = useState([])
  const [state, setState] = useState({ loaded: false })
  const [loadingApi, setLoadingApi] = useState(false)
  const [balance, setBalance] = useState(null)
  const [address, setAddress] = useState(query.address)
  const [experimental, setExperimental] = useState(query.experimental)
  const [decimals, setDecimals] = useState(null)
  const [allowance, setAllowance] = useState(null)
  const [covalApprovedFor, setIsCovalApproved] = useState(0)

  const handlerContract = useContract(contractAddresses.vaultHandler[chainId], contractAddresses.vaultHandlerAbi, true)
  const covalContract = useContract(contractAddresses.coval[chainId], contractAddresses.covalAbi, true)


  const getContractStates = async () => {
    setDecimals(await covalContract.decimals())
    setAllowance(
      await covalContract
        .allowance(account, contractAddresses.vaultHandler[chainId])
        .then((balance: { toString: () => string }) => balance.toString())
    )
    setBalance(await covalContract.balanceOf(account).then((balance: { toString: () => string }) => balance.toString()))
    if (Number(allowance) >= Number(0)) {
      setIsCovalApproved(allowance)
    }
    setState({ loaded: true })
  }

  // const getVaults = async () => {
  //   loadCache()
  //   try {
  //     const response = await fetch(EMBLEM_API + '/vaults/' + (address ? address : account) + (experimental? '?experimental=true' : ''), {
  //       method: 'GET',
  //       headers: {
  //         'Content-Type': 'application/json',
  //         service: 'evmetadata',
  //         chainId: chainId.toString(),
  //       },
  //     })
  //     const jsonData = await response.json()
  //     setState({ loaded: true })
  //     setVaults(jsonData)
  //     saveCache(jsonData)
  //     setLoadingApi(false)
  //     // console.log(jsonData)
  //   } catch (error) {}
  // }

  // const loadCache = () => {
  //   let vaults = JSON.parse(localStorage.getItem((address ? address : account) + '_' + chainId + '_vaults')) // Load vaults from storage before updating from server!
  //   if (vaults) {
  //     setState({ loaded: true })
  //     setVaults(vaults)
  //     setLoadingApi(true)
  //   }
  // }

  // const saveCache = (vaults) => {
  //   localStorage.setItem((address ? address : account) + '_' + chainId + '_vaults', JSON.stringify(vaults)) // Save new state for later
  // }

  const [acct, setAcct] = useState('')
  useEffect(() => {
    if (account && acct != account) {
      setAcct(account)
      setState({ loaded: false })
      getContractStates()
    }
  }, [account, acct])

  const [chain, setChain] = useState(chainId)
  useEffect(() => {
    if (chainId && chain != chainId) {
      setChain(chainId)
      setState({ loaded: false })
      // getVaults()
    }
  }, [chainId, chain])

  useEffect(() => {
    account && chainId ? setState({ loaded: true }) : null
  }, [])

  return (
    <Loader loaded={state.loaded}>
      {loadingApi ? <Refreshing /> : ''}

      <Flex w="100%" justify="center" mt={10}>
        {/* <Stack direction="row" align="flex-start" spacing="0rem" flexWrap="wrap" shouldWrapChildren>
          <Text>Transfer Coval from {chainId == 137 ? "Matic" : "Ethereum"} Network to {chainId == 137 ? "Etherum" : "Matic"} Network</Text>
        </Stack>
        <Stack direction="row" align="flex-start" spacing="0rem" flexWrap="wrap" shouldWrapChildren>
          <Text>Allowance: {allowance * Math.pow(10, -decimals)}</Text>
        </Stack> */}
        <Stack align="center">
          <Box mt="2" ml="4" lineHeight="tight">
            <Text mt={2} as="h4" ml="4" mr="4" fontSize="s" fontStyle="italic" >
            Transfer Coval from {chainId == 137 ? "Matic" : "Ethereum"} Network to {chainId == 137 ? "Etherum" : "Matic"} Network
            </Text>
          </Box>
          <Box mt="2" ml="4" lineHeight="tight">
            <Text mt={2} as="h4" ml="4" mr="4" fontSize="xs" fontStyle="italic" >
            Allowance: {allowance * Math.pow(10, -decimals)}
            </Text>
          </Box>
          <Box mt="2" ml="4" lineHeight="tight">
            <Text mt={2} as="h4" ml="4" mr="4" fontSize="xs" fontStyle="italic" >
            Balance: {balance * Math.pow(10, -decimals)}
            </Text>
          </Box>
        </Stack>
      </Flex>
    </Loader>
  )
}
