import React, { Component } from 'react'
import { Slide } from 'react-slideshow-image';

type ImageProp = {
  image: string
}

type NFTSlideshowProps = {
  items: Array<ImageProp>,
  properties: Object
}

export class NFTSlideshow extends Component<NFTSlideshowProps> {


  // NFTSlideshowProps.images = [
  //   "assets/images/slide_5.jpg",
  //   "assets/images/slide_6.jpg",
  //   "assets/images/slide_7.jpg"
  // ];

  // const fadeProperties = {
  //   duration: 3000,
  //   canSwipe: false,
  // };

  render() {
    let items = this.props.items
    return (
      <div>
        {/* <script>console.log({images})</script> */}
        <h2>Fade Effect</h2>
        <Slide {...this.props.properties}>
          {items.map(item=>{
            console.log(item.image)
            return (
              <div className="each-fade">
                <div>
                  <img src={item.image} />
                </div>
                <p>First Slide</p>
              </div>
            )
          })}
        </Slide>

        {/* <div className="slide-container">
          <Slide {...this.props.properties}>
            <div className="each-fade">
              <div>
                <img src={this.props.images[0]} />
              </div>
              <p>First Slide</p>
            </div>
            <div className="each-fade">
              <p>Second Slide</p>
              <div>
                <img src={this.props.images[1]} />
              </div>
            </div>
            <div className="each-fade">
              <div>
                <img src={this.props.images[2]} />
              </div>
              <p>Third Slide</p>
            </div>
          </Slide>
        </div> */}
      </div>
    );
  }
};

export default NFTSlideshow;
