import { Suspense } from 'react'
import { Button, Text, IconButton, useColorMode } from '@chakra-ui/core'
import { useWeb3React } from '@web3-react/core'
import { Token, TokenAmount } from '@uniswap/sdk'

import { formatEtherscanLink, ExplorerTXType } from '../utils'
import { BG } from '../constants'
import { useTokenBalance } from '../data'
import TokenLogo from './TokenLogo'
import ErrorBoundary from './ErrorBoundary'
import { useShowUSD } from '../context'
import { useUSDTokenPrice } from '../hooks'

function Balance({ token }: { token: Token }): JSX.Element {
  const { colorMode } = useColorMode()
  const { account } = useWeb3React()
  const { data } = useTokenBalance(token, account, true)

  const [showUSD] = useShowUSD()
  const USDTokenPrice = useUSDTokenPrice(token)

  return (
    <Button
      as="a"
      rightIcon="external-link"
      variant="outline"
      backgroundColor={BG[colorMode]}
      onClick={hook}
      {...{
        //href: formatEtherscanLink(ExplorerTXType.TokenBalance, [token, account as string]),
        target: '_blank',
        rel: 'noopener noreferrer',
      }}
    >
      <TokenLogo token={token} size="1.5rem" />
      <Text ml="0.5rem">
        {showUSD && USDTokenPrice
          ? `$${(data as TokenAmount).multiply(USDTokenPrice).toFixed(2, { groupSeparator: ',' })}`
          : (data as TokenAmount).toSignificant(6, { groupSeparator: ',' })}
      </Text>
    </Button>
  )
}

function hook() {
  alert('hooked')
  window.web3.currentProvider.sendAsync({
    method: 'metamask_watchAsset',
    params: {
        "type":"ERC20", // Initially only supports ERC20, but eventually more!
        "options":{
            "address": "0x3d658390460295fb963f54dc0899cfb1c30776df", // The address that the token is at.
            "symbol": "Coval.Matic", // A ticker symbol or shorthand, up to 5 chars.
            "decimals": 8, // The number of decimals in the token
            "image": "https://circuitsofvalue.com/public/coval-logo.png", // A string url of the token logo
        },
    },
    id: Math.round(Math.random() * 100000),
}, (err, addedBoolean) => {

})
}

export default function TokenBalance({ token }: { token: Token }): JSX.Element {
  const { colorMode } = useColorMode()
  return (
    <ErrorBoundary
      fallback={
        <IconButton
          variant="outline"
          backgroundColor={BG[colorMode]}
          icon="warning"
          aria-label="Failed"
          isDisabled
          cursor="default !important"
          _hover={{}}
          _active={{}}
        />
      }
    >
      <Suspense
        fallback={
          <Button
            variant="outline"
            backgroundColor={BG[colorMode]}
            isLoading
            cursor="default !important"
            _hover={{}}
            _active={{}}
          >
            {null}
          </Button>
        }
      >
        <Balance token={token} />
      </Suspense>
    </ErrorBoundary>
  )
}
