import React, { useEffect, useMemo, useState } from 'react'
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  Text,
  Stack,
  Button,
  useColorMode,
  Select,
  Input,
} from '@chakra-ui/core'
import copy from 'copy-to-clipboard'
import QRCode from 'qrcode.react'
import { COLOR, contractAddresses } from '../constants'
import { useWeb3React } from '@web3-react/core'
import { NETWORK_SHORTNAMES } from '../utils'
// import { useContract } from '../hooks'
import { useTokenBalance, useTokenAllowance, useETHBalance } from '../data'
import { Contract, ContractInterface } from '@ethersproject/contracts'
export const EMBLEM_API = 'https://api.emblemvault.io'

export default function OfferModal({
  isOpen,
  onClose,
  contract,
//   addrAddr,
}: {
  isOpen: boolean
  onClose: () => void
  contract
//   addrAddr: string
}): JSX.Element {
//   const address = addrAddr
//   const coin = addrCoin

  const { colorMode } = useColorMode()
  const { account, chainId, library } = useWeb3React()
  const [copied, setCopied] = useState(false)
  const [loading, setLoading] = useState(false)
  const [loaded, setLoaded] = useState(false)
  const [offeringToken, setOfferingToken] = useState({address:'', balance: 0, tokenType:''})
  const [tokenBalances, setTokenBalances] = useState([])
  const [offeringTokenBalance, setOfferingTokenBalance] = useState(0)
  const [acct, setAcct] = useState('')
  const [decimals, setDecimals] = useState()
  const [allowance, setAllowance] = useState()


  const getErc20Contract = (address) =>{
    console.log("contract", address)
    return useContract(address, contractAddresses.covalAbi, true)
  }

  const useContract = (address?: string, ABI?: ContractInterface, withSigner = false): Contract | undefined => {
    return new Contract(address, ABI, withSigner ? library.getSigner(account).connectUnchecked() : library)
  }

  useEffect(() => {
    if (account && acct != account) {
      setAcct(account)
      setLoaded(false)
    }
  }, [account, acct])
  useEffect(() => {
    if (copied) {
      const timeout = setTimeout(() => {
        setCopied(false)
      }, 750)
      return (): void => {
        clearTimeout(timeout)
      }
    }
  }, [copied])

  useEffect(() => {
      if (!loaded && !loading) {
        setLoading(true)
        getBalances(account, async balances =>{
            setTokenBalances(balances)
            setLoading(false)
            setLoaded(true)
        })
      }
  })

  const getContractInfo = async (address)=>{
    let contract = getErc20Contract(address)
    console.log(contract)
    let _dec = await contract.decimals()
    setDecimals(_dec)
    setAllowance(
        await contract
        .allowance(account, contractAddresses.vaultHandler[chainId])
        .then((balance: { toString: () => string }) => balance.toString())
    )
    setOfferingTokenBalance(
        await contract
        .balanceOf(account)
        .then(balance=>(balance * Math.pow(10, -_dec)).toString())
    )
  }



  const getBalances = async (address, cb) => {
    const responce = await fetch(EMBLEM_API + '/'+NETWORK_SHORTNAMES[chainId]+'/balance/' + address, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        service: 'evmetadata',
      },
    })
    const jsonData = await responce.json()
    // console.log(Number(vaultTotalValue), Number(jsonData.totalValue))
    // setVaultTotalValue(Number(vaultTotalValue) + Number(jsonData.totalValue))
    // console.log('get eth balances', jsonData.values)
    return cb(jsonData.values)
  }

  function copyWithFlag(content: string): void {
    copy(content)
    setCopied(true)
  }

  return (
    <Modal isOpen={isOpen} onClose={onClose} isCentered>
      <ModalOverlay />
      <ModalContent color={COLOR[colorMode]}>
        <ModalHeader>
          <Text>Offer</Text>
        </ModalHeader>
        <ModalCloseButton />
        <ModalBody>
          <Stack direction="column">
            <Stack direction="row" align="center" justify="center">
              {/* <QRCode value={coin=="BTC" ? "bitcoin:"+address: address} onClick={() => copyWithFlag(address)} /> */}
            </Stack>

            <Stack mt="6">
              <Text>{account} network: {NETWORK_SHORTNAMES[chainId]}</Text>
                <Select w="100%" value={offeringToken.address}
                    onChange={(e) => {
                    let token = tokenBalances.filter((item)=>{return item.address == e.target.value})[0]
                    setOfferingToken(token)
                    token.tokenType =="ERC-20" ? getContractInfo(token.address) : null
                    console.log(tokenBalances.filter((item)=>{return item.address == e.target.value})[0])
                    console.log(e.target.value)
                    }}
                >
                    <option value="0" >Choose a Token</option>
                    {tokenBalances.map((coin, index) => {
                        if (coin.tokenType !== 'native')
                        return (
                        <option value={coin.address}>{coin.name}</option>
                        )
                    })}
                </Select>
                {offeringToken.balance > 0 && offeringToken.tokenType=="ERC-20" ? (
                    <>
                        <Text>Max {offeringTokenBalance} decimals: {decimals} allowance: {allowance}</Text>
                        <Input></Input>
                    </>
                ) : null}
            </Stack>
            <Stack direction="row" justify="center">
                <Button>Make Offer</Button>
              {/* <Button onClick={() => copyWithFlag('address')}>
                {copied ? <Text>Copied!</Text> : <Text isTruncated>{'address'}</Text>}
              </Button> */}
            </Stack>
          </Stack>
        </ModalBody>
        <ModalFooter></ModalFooter>
      </ModalContent>
    </Modal>
  )
}
