import {
  FormControl,
  FormLabel,
  Stack,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  Text,
  Box,
  Flex,
  Button,
  ButtonGroup,
  Divider,
  Select,
  Image,
  Input
} from '@chakra-ui/core'
import { useRouter } from 'next/router'
import Loader from 'react-loader'
import { useWeb3React } from '@web3-react/core'
import { useEffect, useState } from 'react'
import { EMBLEM_API, curatedContracts, curatedAssets, EMBLEM_V2_API, ORD_API } from '../constants'

let tokenId = null

export default function CreateCurated(props: any) {
  const { query } = useRouter()
  const [tabIndex, setTabIndex] = useState(0)
  const {account, chainId } = useWeb3React()
  const [vaultAddress, setVaultAddress] = useState(account || '')
  const [state, setState] = useState({ loaded: true, private: false })
  const [targetAsset, setTargetAsset] = useState({name: '', image: ''})
  const [targetContract, setTargetContract] = useState({name: '', chain: '', image:(data)=>{return ''}, allowed: (data)=>{return false}, allowedName: (data)=>{return false}})
  const [enableCurated, setEnableCurated] = useState(query.curated == 't')
  const [mintDisabled, setMintDisabled] = useState(true)

  const handleSubmit = (evt: { preventDefault: () => void }) => {
    evt.preventDefault()

    setState({ loaded: false, private: state.private })
    let body = {
      fromAddress: account,
      toAddress: vaultAddress,
      chainId: chainId,
      experimental: true,
      targetContract: targetContract,
      targetAsset: targetAsset
    }
    let apiSuffix = '/create-curated'
    fetch(EMBLEM_V2_API + apiSuffix, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        service: 'evmetadata',
      },
      body: JSON.stringify(body),
    }).then(async function (response) {
      setState({ loaded: true, private: state.private })
      let body = await response.json()
      tokenId = body.data.tokenId || body.data.targetContract.tokenId
      setTimeout(() => {
        location.href = location.origin + '/nft2?id=' + tokenId
      }, 500)
    })
  }

  function getOrdinalRecord(num, cb){
    fetch(ORD_API + 'num/' + num, {
      method: 'GET',
    }).then(async function (response) {
      let body = await response.json()
      return cb(body.data[0])
    })    
  }

  const [acct, setAcct] = useState('')

  useEffect(() => {
    if (account && acct != account) {
      setAcct(account)
      setVaultAddress(account)
    }
  }, [account, acct])

  let typingTimer;

  function handleKeyUpNameInput(e){
    clearTimeout(typingTimer)
    console.log(e.target.value)
    typingTimer = setTimeout(() => {
      let name = `${targetContract.name} ${e.target.value}`
      if (targetContract.allowedName(name)) {
        getOrdinalRecord(e.target.value, (ordRecord)=>{
          if (targetContract.allowed(ordRecord)){
            setMintDisabled(false)
            setTargetAsset({name: name, image: targetContract.image(ordRecord)})
          } else {
            setMintDisabled(true)
            setTargetAsset({name: '', image: ''})
          }
        })        
      } else {
        setMintDisabled(true)
        setTargetAsset({name: '', image: ''})
      }      
    }, 3000)
  }

  return (
    <Loader loaded={state.loaded}>
      <Flex width="full" align="center" justifyContent="center">
        <Box maxW="sm" borderWidth="1px" rounded="lg" overflow="hidden">
          <Tabs defaultIndex={0} index={tabIndex} onChange={(index) => setTabIndex(index)}>
            <TabList>
              <Tab>Create Curated Vault</Tab>
            </TabList>

            <TabPanels>
              <TabPanel>
                <Stack
                  direction="column"
                  align="center"
                  spacing="2rem"
                  flexGrow={1}
                  justifyContent="center"
                  px="2rem"
                  py="2rem"
                  shouldWrapChildren
                >
                  <Stack direction="row" align="flex-start" spacing="0rem" flexWrap="wrap" shouldWrapChildren>
                    <FormControl>

                      <Box p={1} rounded="lg" overflow="hidden">
                        <Stack align="center" p={1}>
                          <Divider />
                          <FormLabel htmlFor="project-selector">Choose Curated Collection</FormLabel>
                          <Select id="project-selector" w="100%" value={targetContract.name}
                            onChange={(e)=>{
                              let contractData:any = curatedContracts.filter(item=>{return item.name == e.target.value})[0]
                              console.log('------', contractData)
                              setTargetContract(contractData)
                            }}
                          >
                            <option value="" >---Choose Project---</option>
                            {curatedContracts.map(project=>{

                              return enableCurated || project.name == 'Emblem Test' || project.name == 'Rare Pepe'? (
                                <option value={project.name} >{project.name}</option>
                              ) : null
                            })}

                          </Select>
                          {targetContract.name && targetContract["_type"] !== 'erc721' ? (
                              <Select id="asset-selector" w="100%" value={targetAsset.name}
                                onChange={(e)=>{
                                  setTargetAsset(curatedAssets[targetContract.chain][targetContract.name].filter(item=>{return item.name == e.target.value})[0])
                                }}
                              >
                                <option value="" >---Choose One---</option>
                                {curatedAssets[targetContract.chain][targetContract.name] ? curatedAssets[targetContract.chain][targetContract.name].map(asset=>{
                                  return (
                                    <option value={asset.name} >{asset.name}</option>
                                  )
                                }): null}

                              </Select>
                            ) : targetContract.name && targetContract["_type"] == 'erc721'? (
                              <Input onKeyUp={(e)=>{ handleKeyUpNameInput(e)}}  w="100%" placeholder={'Inscription Number'} ></Input>
                            ):null}

                            {targetAsset.name && targetAsset.image? (
                                  <Image
                                    p={"20px"}
                                    h={"100%"}
                                    className={"d-block w-100"}
                                    src={targetAsset.image}
                                    width="250px"
                                  />
                                ): null}
                        </Stack>
                      </Box>
                    </FormControl>
                  </Stack>
                  {/* <Stack direction="row" align="flex-start" spacing="0rem" flexWrap="wrap" shouldWrapChildren>
                    {targetAsset.name? (
                      <Text>Confirm: {targetAsset.name.toString()}</Text>
                    ): null}

                  </Stack> */}
                  <Stack direction="row" align="flex-start" spacing="0rem" flexWrap="wrap" shouldWrapChildren>

                    <ButtonGroup spacing={4}>
                      {!account ? (
                        <Button isDisabled type="submit">
                          No Wallet Connected!
                        </Button>
                      ) : (
                        <Button
                        isDisabled={mintDisabled}
                          onClick={(e)=>{                            
                            handleSubmit(e)
                          }} 
                          type="submit">
                          Create {targetContract.name.toString()} Vault
                        </Button>
                      )}
                    </ButtonGroup>
                  </Stack>

                </Stack>
              </TabPanel>
            </TabPanels>
          </Tabs>
        </Box>
      </Flex>
    </Loader>
  )
}
