import React, { Component, useState } from 'react'
import { Box, Flex, Text, Link, Image, Stack, Button, FormControl, FormLabel, Input, FormHelperText, Select } from '@chakra-ui/core'
import { Contract } from '@ethersproject/contracts';
import { contractAddresses } from '../constants';
import { TransactionToast } from './TransactionToast';


type Vault = {
  name: string,
  tokenId: string
}
type PropSettings = {
  vault: {
    mine: boolean,
    status: string,
    tokenId: string,
    name: string,
    offers: Array<Vault>
  },
  myVaults: Array<Vault>,
  nftContract: Contract,
  tradeContract: Contract,
  chainId: number
}



export class TradeControls extends Component<PropSettings> {

  constructor(props) {
    super(props);
  }
  render() {
    const [offeringTokenId, setOfferingTokenId] = useState(null)
    const [offeringTokenAllowed, setOfferingTokenAllowed] = useState(null)
    const [acceptingOfferingIndex, setAcceptingOfferIndex] = useState(null)
    const [acceptingTokenAllowed, setAcceptingTokenAllowed] = useState(null)
    const [acceptingTokenId, setAcceptingTokenId] = useState(null)
    const [hash, setHash] = useState(null)

    async function checkOfferingAllowance(tokenId) {
      let allowed = await this.props.nftContract.getApproved(tokenId)
      setOfferingTokenAllowed(allowed != "0x0000000000000000000000000000000000000000")
    }

    async function checkAcceptingAllowance(tokenId) {
      let allowed = await this.props.nftContract.getApproved(tokenId)
      setAcceptingTokenAllowed(allowed != "0x0000000000000000000000000000000000000000")
    }

    const approveOffer = (tokenId) => {
      // debugger
      console.log("checking offer for", tokenId)
        ; (this.props.nftContract as Contract)
          .approve(contractAddresses.nftrade[this.props.chainId], (tokenId && typeof tokenId === "string" ? tokenId : offeringTokenId))
          .then(({ hash }: { hash: string }) => {
            setHash(hash)
          })
      // .catch((error: ErrorWithCode) => {
      //   if (error?.code == 4001) {
      //     setApproving(false)
      //   }
      // })
    }

    const makeOffer = (tokenId) => {
      ; (this.props.tradeContract as Contract)
        .addOffer(contractAddresses.emblemVault[this.props.chainId], offeringTokenId, contractAddresses.emblemVault[chainId], tokenId)
        .then(({ hash }: { hash: string }) => {
          setHash(hash)
        })
      // .catch((error: ErrorWithCode) => {
      //   if (error?.code == 4001) {
      //     setApproving(false)
      //   }
      // })
    }

    const acceptOffer = () => {
      ; (this.props.tradeContract as Contract)
        .acceptOffer(contractAddresses.emblemVault[this.props.chainId], acceptingTokenId, Number(acceptingOfferingIndex))
        .then(({ hash }: { hash: string }) => {
          setHash(hash)
        })
      // .catch((error: ErrorWithCode) => {
      //   if (error?.code == 4001) {
      //     setApproving(false)
      //   }
      // })
    }

    const rejectOffer = () => {
      ; (this.props.tradeContract as Contract)
        .rejectOffer(contractAddresses.emblemVault[this.props.chainId], acceptingTokenId, Number(acceptingOfferingIndex))
        .then(({ hash }: { hash: string }) => {
          setHash(hash)
        })
      // .catch((error: ErrorWithCode) => {
      //   if (error?.code == 4001) {
      //     setApproving(false)
      //   }
      // })
    }

    const flexSettings = {
      flex: '1',
      minW: '300px',
      maxW: '300px',
      borderWidth: '1px',
      color: 'white',
      mx: '6',
      mb: '6',
      rounded: 'lg',
      overflow: 'hidden',
      borderColor: this.props.vault.status == 'claimed' ? 'green !important' : '',
      cursor: 'pointer',
    }
    return (
      <Box {...flexSettings}>
        {!this.props.vault.mine ? <Select p={2} m={2} w="94%" placeholder="Offer NFT" onChange={e => {
          let tokenId = e.target.value; // console.log(e.target.value)

          setOfferingTokenId(tokenId);
          checkOfferingAllowance(tokenId);
        }}>
          {this.props.myVaults.map((vault, index) => {
            return <option key={index} value={this.props.vault.tokenId}>{vault.name}</option>;
          })}
        </Select> : <Select p={2} m={2} w="94%" placeholder="Offers" onChange={e => {
          let index = e.target.value;
          console.log(e.target.value);
          setAcceptingOfferIndex(index);
          setAcceptingTokenId(this.props.vault.tokenId);
          checkAcceptingAllowance(this.props.vault.tokenId);
        }}>
            {this.props.vault.offers.map((offer, index) => {
              return <option key={index} value={index}>{Number(offer.tokenId)}</option>;
            })}
          </Select>}
        {offeringTokenId && !this.props.vault.mine ? offeringTokenAllowed ? <Button m={2} onClick={() => {
          makeOffer(this.props.vault.tokenId);
        }}>Make Offer</Button> : <Button m={2} w="94%" onClick={approveOffer}>Approve</Button> : null}
        {acceptingOfferingIndex && this.props.vault.mine ? acceptingTokenAllowed ? <Button m={2} w="94%" onClick={() => {
          acceptOffer();
        }}>Accept Offer</Button> : <Button m={2} onClick={() => {
          approveOffer(this.props.vault.tokenId);
        }}>Approve</Button> : null}
        {acceptingOfferingIndex && this.props.vault.mine ? <Button m={2} w="94%" onClick={() => {
          rejectOffer();
        }}>Reject Offer</Button> : null}
        {hash ? (
      <TransactionToast
          hash={hash}
          onComplete={() => {
            setHash(null)
          }}
        />
      ): null }
      </Box>
    );
  }
}
export default TradeControls
