            let infoContainerSettings = {
              borderColor : '',
              flex: '1',
              minW: '200px',
              maxW: '200px',
              borderWidth: '1px',
              // color: 'white',
              mx: '6',
              mb: '6',
              rounded: 'lg',
              overflow: 'hidden'
            }
            const redirect = function () {
              setLoadingApi(true)
              location.href = url
            }
            return (
