import React, { Component } from 'react'
import {
  Box,
  Image,
  Text,
  Link,
  useColorMode,
  Button,
  Flex,
  PseudoBox
} from '@chakra-ui/core'

import {HStack, VStack, Circle } from '@chakra-ui/react'
import { Contract } from '@ethersproject/contracts'

const getContract = (address, abi, library, account) =>{
  return new Contract(address, abi, library.getSigner(account).connectUnchecked())
}

function checkApproval(handler: string, contract: string, abi: any, library: any, account: any) {
  return false
}

type AttributesProps = {
  handler: string,
  contract: string,
  approvalType: string,
  abi: any,
  label: string,
  library: any,
  account: any,
}

export class Approval extends Component<AttributesProps> {
  render() {
    let handler = this.props.handler
    let contract = this.props.contract
    let label = this.props.label
    let approvalType = this.props.approvalType
    let abi = this.props.abi
    let library = this.props.library
    let account = this.props.account
    let approved = checkApproval(handler, contract, abi, library, account)
    return approved? (
      <></>
    ) : (
      <Flex w="100%" flexWrap="wrap" mt={3} >
        <Button backgroundColor={"#02b402"} color={"black !important"} fontWeight={"bold !important"} width="100%">
          {label}
        </Button>
      </Flex>
    )
  }
}

export default Approval
