import { Box, Flex, Text, Link, Image, Stack, Spinner } from '@chakra-ui/core'
import Loader from 'react-loader'
import Refreshing from './Refreshing'
import { useRouter } from 'next/router'
import { useWeb3React } from '@web3-react/core'
import { useEffect, useState } from 'react'
import { validImage } from '../utils'
import { EMBLEM_API } from '../constants'

export default function SwapChain() {
  const { query } = useRouter()
  const { account, chainId } = useWeb3React()
  // const [vaults, setVaults] = useState([])
  const [state, setState] = useState({ loaded: false })
  const [loadingApi, setLoadingApi] = useState(false)
  const [balance, setBalance] = useState(null)
  const [address, setAddress] = useState(query.address)
  const [experimental, setExperimental] = useState(query.experimental)

  // const getVaults = async () => {
  //   loadCache()
  //   try {
  //     const response = await fetch(EMBLEM_API + '/vaults/' + (address ? address : account) + (experimental? '?experimental=true' : ''), {
  //       method: 'GET',
  //       headers: {
  //         'Content-Type': 'application/json',
  //         service: 'evmetadata',
  //         chainId: chainId.toString(),
  //       },
  //     })
  //     const jsonData = await response.json()
  //     setState({ loaded: true })
  //     setVaults(jsonData)
  //     saveCache(jsonData)
  //     setLoadingApi(false)
  //     // console.log(jsonData)
  //   } catch (error) {}
  // }

  // const loadCache = () => {
  //   let vaults = JSON.parse(localStorage.getItem((address ? address : account) + '_' + chainId + '_vaults')) // Load vaults from storage before updating from server!
  //   if (vaults) {
  //     setState({ loaded: true })
  //     setVaults(vaults)
  //     setLoadingApi(true)
  //   }
  // }

  // const saveCache = (vaults) => {
  //   localStorage.setItem((address ? address : account) + '_' + chainId + '_vaults', JSON.stringify(vaults)) // Save new state for later
  // }

  const [acct, setAcct] = useState('')
  useEffect(() => {
    if (account && acct != account) {
      setAcct(account)
      setState({ loaded: false })
      // getVaults()
    }
  }, [account, acct])

  const [chain, setChain] = useState(chainId)
  useEffect(() => {
    if (chainId && chain != chainId) {
      setChain(chainId)
      setState({ loaded: false })
      // getVaults()
    }
  }, [chainId, chain])

  useEffect(() => {
    account && chainId ? setState({ loaded: true }) : null
  }, [])

  return (
    <Loader loaded={state.loaded}>
      {loadingApi ? <Refreshing /> : ''}

      <Flex w="100%" justify="center" flexWrap="wrap" mt={10}>
        <Text>Transfer Coval from {chainId == 137 ? "Matic" : "Ethereum"} Network to {chainId == 137 ? "Etherum" : "Matic"} Network</Text>
      </Flex>
    </Loader>
  )
}
