import React, { Component } from 'react'
import {
  Box,
  Image,
  Text,
  Link,
  useColorMode,
  Button,
  Flex,
  PseudoBox
} from '@chakra-ui/core'

import {HStack, VStack, Circle } from '@chakra-ui/react'

type ListingsProps = {
  listings: any[],
  colorMode: string,
}

export class Listings extends Component<ListingsProps> {
  render() {
    let colorMode = this.props.colorMode
    let listings = this.props.listings
    return (
      <Flex w="100%" flexWrap="wrap" mt={3} >
        {
          listings.map((listing, index)=>{
            return (

              <Box mt="2px" p={1} ml="3px" minWidth="45%" /*maxWidth="50%"*/ width="98%" className="NFT newest coin-balance-content" key={0}  >
                <Text ml={2} mr={Number.isInteger(index/2) ? 0: "4px"} fontSize=".9em" textAlign={Number.isInteger(index/2) ? "left": "right"} fontWeight="bold" overflow="hidden" isTruncated={true} >{listing.price.amount.decimal} {listing.price.currency.name}</Text>
                <Text ml={2} mr={Number.isInteger(index/2) ? 0: "4px"} fontSize=".9em" textAlign={Number.isInteger(index/2) ? "left": "right"} fontWeight="bold" overflow="hidden" isTruncated={true} >{listing.source.name}</Text>

              </Box>
            )
          })
        }
      </Flex>
    )
  }
}

export default Listings
