import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Checkbox } from '@chakra-ui/core'

import { useWeb3React } from '@web3-react/core'
import { useState } from 'react'
import { EMBLEM_API } from '../../constants'
import { fromContractValue, CHAIN_ID_NAMES } from '../../utils'

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

function createData(name: string, calories: number, fat: number, carbs: number, protein: number) {
    return { name, calories, fat, carbs, protein };
}



let rows = [
    //   createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    //   createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    //   createData('Eclair', 262, 16.0, 24, 6.0),
    //   createData('Cupcake', 305, 3.7, 67, 4.3),
    //   createData('Gingerbread', 356, 16.0, 49, 3.9),
];

export default function BridgeDeposits() {
    const { account, chainId } = useWeb3React()
    const [showPending, setShowPending] = useState(true)
    const [hasDataRows, setHasDataRows] = useState(false)
    const [dataRows, setDataRows] = useState([])
    const classes = useStyles();
    !hasDataRows ? (
        getDeposits(deposits => {
            setHasDataRows(true)
            setDataRows(deposits)
        })
    ) : null
    return (
        <>
            <Checkbox size="md" onClick={(e)=>{
                setShowPending(!showPending)
                setHasDataRows(false)
            }} defaultIsChecked>
                Show Pending
            </Checkbox>
            <TableContainer component={Paper}>
                <Table className={classes.table} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell>Dessert (100g serving) {chainId}</TableCell>
                            <TableCell align="right">Calories</TableCell>
                            <TableCell align="right">Fat&nbsp;(g)</TableCell>
                            <TableCell align="right">Carbs&nbsp;(g)</TableCell>
                            <TableCell align="right">Protein&nbsp;(g)</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {dataRows.map((row) => (
                            <TableRow key={row.name}>
                                <TableCell component="th" scope="row">
                                    {Number(fromContractValue(row.returnValues.amount, 'gwei')*10).toLocaleString()}
                                </TableCell>
                                <TableCell align="right">{row.blockNumber}</TableCell>
                                <TableCell align="right">{row.transactionHash}</TableCell>
                                <TableCell align="right">{CHAIN_ID_NAMES[row.returnValues.chainId]}</TableCell>
                                <TableCell align="right">{row.protein}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[5, 10, 25]}
                component="div"
                count={rows.length}
                rowsPerPage={5}
                page={0}
            />
        </>
    );

    async function getDeposits(cb) {

        const responce = await fetch(EMBLEM_API + '/transfersOutDb?pending='+showPending, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'chainid': chainId.toString(),
                'service': 'evmetadata',
            },
        })
        const jsonData = await responce.json()
        return cb(jsonData)
    }
}
