var getNativeAPIRef = (API) => (window[backup] ? window[backup][API] : window[API]);

var createProgressEvent = (loaded, lengthComputable = true) => {
    // assigning 0 to 1 somehow doesn't work
    const type = 'progress';
    const data = {
        loaded: loaded * 100,
        total: 100,
        lengthComputable,
