import { Box, Flex, Text, Link, Image, Stack, Spinner } from '@chakra-ui/core'
import Loader from 'react-loader'
import Refreshing from './Refreshing'
import { useRouter } from 'next/router'
import { useWeb3React } from '@web3-react/core'
import { useEffect, useState } from 'react'
import { validImage } from '../utils'
import { Contract } from '@ethersproject/contracts'
import { EMBLEM_API, BURN_ADDRESS, contractAddresses } from '../constants'
import { useContract } from '../hooks'

export default function LeaderBoard() {
  const { query } = useRouter()
  const { account, chainId } = useWeb3React()
  const [vaults, setVaults] = useState([])
  const [state, setState] = useState({ loaded: false })
  const [loadingApi, setLoadingApi] = useState(false)
  const [loadingOwners, setLoadingOwners] = useState(false)
  const [address, setAddress] = useState(query.address)
  const [owners, setOwners] = useState({})
  const [totalSupply, setTotalSupply] = useState(0)


  const emblemContract = useContract(contractAddresses.emblemVault[chainId], contractAddresses.emblemAbi, true)

  const getVaults = async () => {
    getOwners()
    loadCache()
    // try {
    //   const response = await fetch(EMBLEM_API + '/vaults/' + (address ? address : account), {
    //     method: 'GET',
    //     headers: {
    //       'Content-Type': 'application/json',
    //       service: 'evmetadata',
    //       chainId: chainId.toString(),
    //     },
    //   })
    //   const jsonData = await response.json()
    //   setState({ loaded: true })
    //   setVaults(jsonData)
    //   saveCache(jsonData)
    //   setLoadingApi(false)
    //   // console.log(jsonData)
    // } catch (error) {}
  }

  const getOwners = async () => {
    if (!loadingOwners) {
      setOwners({})
      setLoadingOwners(true)
    } else {
      return
    }
    let totalSupply = await (emblemContract as Contract).totalSupply()
    setTotalSupply(totalSupply.toString())
    console.log(totalSupply)
    getTokensByIndex(Number(totalSupply.toString()), 0, () => {
      console.log('owners', owners)
      setLoadingOwners(false)
      saveCache(owners)
    })
  }

  async function getTokensByIndex(count, index, cb) {
    let tokenId = await (emblemContract as Contract).tokenByIndex(index)
    console.log('tokenId', tokenId.toString(), index)
    getOwner(tokenId.toString(), ()=>{
      if (count - 1 < index + 1) {
          return cb()
        } else {
          return getTokensByIndex(count, index + 1, cb)
        }
    })
  }

  async function getOwner(tokenId, cb) {
    let owner = await (emblemContract as Contract).ownerOf(tokenId)
    if (owner === '0x0000000000000000000000000000000000000000' || owner.toLowerCase() === '0x5d152dd902cc9198b97e5b6cf5fc23a8e4330180') {
      return cb()
    }
    if (!owners[owner]) {
      owners[owner] = [{ tokenId: tokenId}]
      setOwners(owners)
    } else {
      owners[owner].push({ tokenId: tokenId.toString() })
      setOwners(owners)
    }
    return cb()
  }

  const loadCache = () => {
    let owners = JSON.parse(localStorage.getItem((address ? address : account) + '_' + chainId + '_leader')) // Load vaults from storage before updating from server!
    if (owners) {
      setState({ loaded: true })
      setOwners(vaults)
      setLoadingOwners(true)
    }
  }

  const saveCache = (owners) => {
    localStorage.setItem((address ? address : account) + '_' + chainId + '_leader', JSON.stringify(owners)) // Save new state for later
  }

  const [acct, setAcct] = useState('')
  // useEffect(() => {
  //   if (account && acct != account) {
  //     setAcct(account)
  //     setState({ loaded: false })
  //     getVaults()
  //   }
  // }, [account, acct])

  const [chain, setChain] = useState(chainId)
  useEffect(() => {
    if (chainId && chain != chainId) {
      setChain(chainId)
      setState({ loaded: false })
      getVaults()
    }
  }, [chainId, chain])

  useEffect(() => {
    account && chainId ? getVaults() : setState({ loaded: true })
  }, [])

  return (
    <Loader loaded={state.loaded}>
      {loadingOwners ? <Refreshing /> : ''}
      <Flex width="full" align="center" justifyContent="center">
        <Stack>
          {Object.keys(owners).map((owner, index) => {
            return (
            <Stack key={index}>  
              <Text key={index}>Address: {owner} Vaults: {owners[owner].length}</Text>              
            </Stack>
            )
          })}
        </Stack>        
      </Flex>
    </Loader>
  )
}
