import { Box, Flex, Text, Link, Image, Stack, Button, BoxProps, Input, Select, Tabs, Tab, TabList, TabPanels, TabPanel } from '@chakra-ui/core'
import Loader from 'react-loader'
import Refreshing from './Refreshing'
import { useRouter } from 'next/router'
import { useWeb3React } from '@web3-react/core'
import { useEffect, useState } from 'react'
import InfiniteScroll from 'react-infinite-scroll-component';
import {HStack, VStack, Circle } from '@chakra-ui/react'

export default function PnL() {
  const { query } = useRouter()
  const { account, chainId } = useWeb3React()
  const [state, setState] = useState({ loaded: false })
  const [loadingApi, setLoadingApi] = useState(false)
  const [stats, setStats] = useState([])
  const [address, setAddress] = useState(query.address || "0x3B31925EeC78dA3CF15c4503604c13b0eEBC57e5")
  const [offset, setOffset] = useState(0)
  const [limit, setLimit] = useState(10)
  const [shouldFetchData, setShouldFetchData] = useState(false)
  const [hasMore, setHasMore] = useState(true)

  interface ErrorWithCode extends Error {
    code?: number
  }

  useEffect(() => {
    if (state.loaded == false && !loadingApi) {
      setLoadingApi(false)
      getPnL()
    }
  }, [])

  useEffect(() => {
    if (shouldFetchData) {
      console.log("Should load api after setting consts", offset)
      setShouldFetchData(false)
      getPnL()
    } else {
      console.log("wtf")
    }
  }, [shouldFetchData, offset])

  const fetchData = async() =>{
    console.log('setting new offset to', offset+limit)
    setOffset(offset+limit)
    setShouldFetchData(true)
  }

  const getPnL = async() =>{
    console.log("getting logs")
    fetch('https://api.emblemvault.io/s:osautomate/offer/'+address+'?limit='+limit+'&offset='+offset, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(async function (response) {
      setLoadingApi(false)
      setState({ loaded: true })
      let jsonData = await response.json()
      setStats(stats.concat(jsonData.intelligences))
      console.log(jsonData)
      if (jsonData.intelligences.length == 0) {
        setHasMore(false)
      }
    })
  }

  return (
    <Loader loaded={state.loaded}>
      {loadingApi ? <Refreshing /> : ''}

      <Flex w="100%" justify="center" mt={10}>
        <Stack align="center" w={"75%"}>
          {stats.length > 0? (
            <>
              {/* <Text>Total Offers in USD: ${stats.offers.toFixed(2)}</Text>
              <Text>Total Number of offers: ${stats.offerCount.toFixed(2)}</Text> */}
              <InfiniteScroll
                className="infinite-scroll"
                height={200}
                dataLength={stats.length} //This is important field to render the next data
                next={fetchData}
                hasMore={hasMore}
                loader={<Refreshing />}
                endMessage={
                  <p style={{ textAlign: 'center' }}>
                    <b>Yay! You have seen it all</b> 
                  </p>
                }
              >
                {stats.map((item, index)=>{
                  return (
                    <>
                      <HStack key={"container-"+index} p={2} >
                        <Box className="coin-image-container" m={5} min-width="40px">
                          { item.image ? (
                            <Image width="40px" src={item.image}></Image>
                          ) : (
                            <Circle size="40px" bg="gray" color="white" isTruncated>
                              {item.collection[0]}
                            </Circle>
                          ) }
                        </Box>
                        <Text fontSize="x-small" key={index}>{item.collection} : {item.name? item.name : '#'+item.tokenId}</Text>
                      </HStack>
                    </>
                  )
                })}
              </InfiniteScroll>
            </>
          ) : null }
        </Stack>
      </Flex>

    </Loader>
  )
}
