  // alert(EMBLEM_API)
  const [batchName, setBatchName] = useState("");
  const [imageBaseUrl, setImageBaseUrl] = useState("");
  const [inscriptionBaseUrl, setInscriptionBaseUrl] = useState("");
  const [addressType, setAddressType] = useState("");
  const [titleTemplate, setTitleTemplate] = useState("");
  const [descriptionTemplate, setDescriptionTemplate] = useState("");
  const [total, setTotal] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    var myHeaders = new Headers();
myHeaders.append("Content-Type", "application/json");

var raw = JSON.stringify({
  "batchName": "exampleBatch2",
  "imageBaseUrl": "www.example.com/image",
  "inscriptionBaseUrl": "www.example.com/inscription",
  "addressType": "exampleAddressType",
  "titleTemplate": "exampleTitleTemplate",
  "descriptionTemplate": "exampleDescriptionTemplate",
  "total": 0,
  "batch_id": 7
});

var requestOptions: any = {
  method: 'POST',
  headers: myHeaders,
  body: raw,
  redirect: 'follow'
};

fetch("http://localhost:3001/addBatch", requestOptions)
