import React, { Component, useEffect, useState } from 'react'
import {
  Box,
  Image,
  Text,
  Link,
  useColorMode,
  Button,
  Flex,
  PseudoBox
} from '@chakra-ui/core'

import {HStack, VStack, Circle } from '@chakra-ui/react'
import { Contract } from '@ethersproject/contracts'
import { useWeb3React } from '@web3-react/core'

const getContract = (address, abi, library, account): any =>{
  return new Contract(address, abi, library.getSigner(account).connectUnchecked())
}



type AttributesProps = {
  handler: {address: string, abi: any},
  spending: {address: string, abi: any},
  amount: number,
  label: string,
  library: any,
  account: any,
}
export default function Approval(props: AttributesProps) {

  const { account } = useWeb3React()
  const [allowance, setAllowance] = useState(false)

  useEffect(()=>{
      console.log('--------------',account)
      if (!allowance) {
        checkApproval(props.handler, props.spending, props.library, props.account, props.amount)
        console.log('--------------', allowance)
      }
  })

  return account? (
      <>
          {allowance}
      </>
  ): null

  function checkApproval(handler: { address: string; abi: any }, spending: { address: string; abi: any }, library: any, account: any, amount: number) {
    if (amount > 0) {
        let spendingContract = getContract(spending.address, spending.abi, library, account)
        spendingContract.allowance(account, handler.address).then((allowance: any) => {
          setAllowance(allowance.toString())
        })        
    }
  }
}
